import React, { useState } from "react";
import NewExpenseForm from "./components/expense/NewExpense/NewExpenseForm";
import ExpenseDetails from "./components/expense/ExpenseDetails";

const dummyObj = [
  {
    id: 1,
    LocationOfExpenditure: "basic needs",
    title: "Food",
    amount: 1,
    date: new Date(2021, 2, 28),
  },
  {
    id: 2,
    LocationOfExpenditure: "essential needs",
    title: "Food",
    amount: 12,
    date: new Date(2020, 7, 14),
  },
  {
    id: 3,
    LocationOfExpenditure: "not essential needs",
    title: "Food",
    amount: 13,
    date: new Date(2021, 5, 12),
  },
  {
    id: 4,
    LocationOfExpenditure: " needs",
    title: "Food",
    amount: 14,
    date: new Date(2021, 5, 12),
  },
  { id: 5, LocationOfExpenditure: "no needs", title: "Food", amount: 15 },
];

function App() {
  const [objs, setObj] = useState(dummyObj);

  const expenseFromNewExpenseForm = (obj) => {
    console.log(obj);
    setObj((previousObj) => {
      return [obj, ...previousObj];
    });
  };

  return (
    <div>
      <NewExpenseForm onAddExpense={expenseFromNewExpenseForm} />
      <ExpenseDetails items={objs} />
    </div>
  );
}

export default App;
