import React, { useState } from "react";
import ExpenseItems from "./ExpenseItems";
import Card from "./Card";
import ExpensesFilter from "./ExpensesFilter";
import "./ExpenseDetails.css";

const ExpenseDetails = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  // Filter items based on the selected year
  const filteredItems = props.items.filter((obj) => {
    // Assuming obj.date is a string in the format "YYYY-MM-DD"
    return obj.date.startsWith(filteredYear);
  });

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={filterChangeHandler}
        />
        {filteredItems.map((obj) => (
          <ExpenseItems
            key={obj.id} // Make sure to include a unique key
            title={obj.title}
            amount={obj.amount}
            date={obj.date}
          />
        ))}
      </Card>
    </div>
  );
};

export default ExpenseDetails;
