import React, { useState } from "react";
import NewExpenseForm from "./components/expense/NewExpense/NewExpenseForm";
import ExpenseDetails from "./components/expense/ExpenseDetails";

const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];
function App() {
  const [objs, setObj] = useState(DUMMY_EXPENSES);

  const expenseFromNewExpenseForm = (obj) => {
    console.log(obj);
    setObj((previousObj) => {
      return [obj, ...previousObj];
    });
  };

  return (
    <div>
      <NewExpenseForm onAddExpense={expenseFromNewExpenseForm} />
      <ExpenseDetails items={objs} />
    </div>
  );
}

export default App;
