import React, { useState } from "react";
import ExpenseDate from "../expense/ExpenseDate";
import "../ExpenseItems.css";
import ExpenseDetails from "./ExpenseDetails.js";
import ExpenseFilter from "./NewExpense/ExpenseFilter.js";
const ExpenseItems = (props) => {
  const [amount, setTitle] = useState(props.amount);
  const clickHandler = () => {
    setTitle(100);
    console.log(amount);
    const Expense = (props) => {
      const [filterYear, setFilteredYear] = useState("2023");
      const filterChangeHandler = (selectedYear) => {
        setFilteredYear(selectedYear);
      };
    };
  };
  return (
    <div className="expense-item">
      <ExpenseDate />
      <ExpenseFilter
        selected={filterYear}
        onChangeFilter={filterChangeHandler}
      ></ExpenseFilter>
      <ExpenseDetails
        amount={amount}
        LocationOfExpenditure={props.LocationOfExpenditure}
        title={props.title}
      />
      <button onClick={clickHandler}>Amount</button>
    </div>
  );
};

export default ExpenseItems;
